import React from 'react'
import "../styles/main.css"
import { useState } from 'react';
import Card from './Card';

const Main = () => {

  const [file, setFile] = useState();
//   const [clothes,setClothes] = useState()

  function fileChangeHandler(e) {
      console.log(e.target.files);
      setFile(URL.createObjectURL(e.target.files[0]));
  }
  
  const clothes = [
    {
        imgsrc:"https://img.kwcdn.com/product/Fancyalgo/VirtualModelMatting/17be2cb25fbaf290429dfe49b018dd7b.jpg?imageMogr2/auto-orient%7CimageView2/2/w/800/q/70/format/webp",
        name:"Summer Clothes",
        id:1
    },
    {
        imgsrc: "data:image/jpeg;base64,",
        name: "Floral Dress",
        id:2
    },
    {
        imgsrc: "https://qph.cf2.quoracdn.net/main-qimg-075434771370ded287d2cb7232fb413e-lq",
        name: "Formal White Shirt",
        id:3
    },
    {
        imgsrc: "https://lp2.hm.com/hmgoepprod?set=format%5Bwebp%5D%2Cquality%5B79%5D%2Csource%5B%2F87%2F7d%2F877d125fd799a5ecbafb32042cd685bbc6a35592.jpg%5D%2Corigin%5Bdam%5D%2Ccategory%5B%5D%2Ctype%5BLOOKBOOK%5D%2Cres%5Bm%5D%2Chmver%5B1%5D&call=url%5Bfile%3A%2Fproduct%2Fmain%5D",
        name: "Grey Loose Fit Zip-through",
        id:4
    },
    {
        imgsrc: "data:image/jpeg;base64,",
        name: "Three Piece Suit - Black",
        id:5
    },
    {
        imgsrc: "data:image/jpeg;base64,",
        name: "Locomoto Female dress",
        id:6
    },
    {
        imgsrc: "https://media.istockphoto.com/id/993728724/photo/full-length-man-mannequin.jpg?s=612x612&w=0&k=20&c=ivbhIbcK8djPIviHLCPt1kjWlbDBslgQkd1Z02hAKmk=",
        name: "Summer casual Shirt",
        id:7
    }

  ]


  return (
    
    <>
    <div className='main'>
        <div className="imageUpload">
            <div className="preview">
                
                {
                    file?
                    (
                        <img src={file} alt="previewImg" />
                    ):
                    (
                        <h3>Upload your image</h3>
                    )
                }
            </div>
            <input type="file" id="imgUpload" onChange={fileChangeHandler}/>
        </div>
        <div className="clothes">
            {clothes.map((i)=>(
                <a href={i.imgsrc}>
                <Card key={i.id} name={i.name} imgsrc={i.imgsrc}/>
                </a>
            ))}
        </div>
        <div className="menu">
            menu
        </div>
        
    </div>
    <div className="result">
        <img src={file} alt="result img" id='resultImg'/>
    </div>
    </>

    
  )
}

export default Main